import Button from "../Button";
import Card from "../Card";
import "./style.css";

function ErrorModal(props) {
  return (
    <Card className='modal'>
      <header>
        <h2>{props.title}</h2>
      </header>
      <div>
        <p>{props.message}</p>
      </div>
      <footer>
        <Button>Okay</Button>
      </footer>
    </Card>
  );
}

export default ErrorModal;
